echo "CREATE TABLE contents(id integer, title text, url text, description text, tag text, content text, created_at TEXT NOT NULL DEFAULT (DATETIME('now', 'localtime')), updated_at TEXT NOT NULL DEFAULT (DATETIME('now', 'localtime')));CREATE TRIGGER trigger_contents_updated_at AFTER UPDATE ON contents BEGIN UPDATE contents SET updated_at = DATETIME('now', 'localtime') WHERE rowid == NEW.rowid;END;"